function benchmark($function, $iterations = 1000)
{
    $start = microtime(true);
    for ($i = 0; $i < $iterations; ++$i) {
        $function();
    }
    return (microtime(true) - $start) / $iterations;
}

function doBenchmark($function, $iterations=1000)
{
    echo "benchmarking $function... ";
    $result = benchmark($function, $iterations);
    $result *= 1000;
    echo "$result [ms] (average of $iterations iterations)\n";
}

